onSave(): void {
   this.blog = this.blogFg.value
   if (this.action === 'Edit') {
      this.blogsService.updateAngularBlog(this.blogId, this.blog)
         .pipe(takeUntil(this.destroy$))
         .subscribe({
            next: (res: BlogInterface) => {
               this.toastr.success(
                  res.subTitle,
                  res.title + 'it was successfully updated.'
               )
            },
            error: (err) => {
               this.toastr.error(err, 'Error saving your reply'
               )
            },
            complete: () => {
               this.toastr.info(
                  'Observable was destroyed successfully.',
                  'updateAngularBlog observable destroy$ invoked.',
                  {
                     positionClass: 'toast-top-right',
                     newestOnTop: true
                  })
            }
         })
   } else {
      this.blogsService.postBlog(this.blogFg.value.type.at(0).itemName, this.blog)
         .pipe(takeUntil(this.destroy$))
         .subscribe({
            next: (blog: BlogInterface) => setTimeout(() => {
               this.toastr.success(
                  blog.subTitle,
                  blog.title + 'was successfully created.'
               )
            }),
            error: (err: HttpErrorResponse) => {
               setTimeout(() => this.toastr.error(err.message, 'an error occurred'))
            },
            complete: () => {
               this.toastr.info(
                  'Observable was destroyed successfully.',
                  'JavaScript Blog observable destroy$ invoked.',
                  {
                     positionClass: 'toast-top-right',
                     newestOnTop: true
                  })
            }
         })
   }
}

ngOnInit() {
   const body = document.getElementsByTagName( 'body' )[0];
   body.classList.add( 'blog-posts' );
   const navbar = document.getElementsByTagName( 'nav' )[0];
   navbar.classList.remove( 'navbar-transparent' );
   navbar.classList.add( 'bg-danger' );
   
   this.articlesService.getAngularBlog().subscribe( blog => {
      this.articles = blog[0].articles;
      console.log('articles: ', blog[0].articles);
   } )
}

