import { Injectable } from "@angular/core"
import { HttpClient } from "@angular/common/http"
import {
   map,
   Observable
} from "rxjs"
import {
   BlogFooterInterface,
   BlogInterface, MetadataInterface
} from "../../../models/blogInterface"

@Injectable()
export class BlogsService {

   constructor(private http: HttpClient) {
   }

   /**
    * GET: Metadata by Id
    * @param {string} id
    * @returns {Observable<MetadataInterface>}
    */
   getMetadataById(id: string): Observable<MetadataInterface> {
      return this.http.get<MetadataInterface>('http://localhost:3000/api/metadata/' + id)
   }

   /**
    * GET: All Angular blogs
    */
   getBlogByName(blog: string): Observable<BlogInterface[]> {
      return this.http.get<BlogInterface[]>('http://localhost:3000/' + blog)
   }

   /**
    * GET: All Angular blogs
    */
   getAngularBlogs(): Observable<BlogInterface[]> {
      return this.http.get<BlogInterface[]>('http://localhost:3000/ng')
   }

   /**
    * GET: All blogs
    * @returns {Observable<BlogInterface[]>}
    */
   getBlogsSorted(blog: string): Observable<BlogInterface[]> {
      return this.http.get<BlogInterface[]>('http://localhost:3000/' + blog)
         .pipe(
            map((response) => response.sort((a: BlogInterface, b: BlogInterface) => {
               return new Date(a.modified).getDate() - new Date(b.modified).getDate()
            })
               .reverse())
         )
   }

