   /**
    * GET: Angular blog by Id
    * @param blog
    * @param id unique identifier
    */
   getBlogById(blog: string, id: string): Observable<BlogInterface> {
      return this.http.get<BlogInterface>(`http://localhost:3000/${ blog }/${ id }`)
   }

   /**
    * UPDATE: Angular blog
    * @param id
    * @param updateBody
    */
   updateAngularBlog(id: string, updateBody: BlogInterface): Observable<BlogInterface> {
      return this.http.put<BlogInterface>(`http://localhost:3000/ng/${ id }`, updateBody)
   }

